package moocfihelsinki;

import java.util.Scanner;

import moocfihelsinki.account.Account;
import moocfihelsinki.whistle.Whistle;
import moocfihelsinki.paymentcard.PaymentCard;
import moocfihelsinki.items.Items;

public class App {
    public String getGreeting() {
        return "Hello World!";
    }

    public static void main(String[] args) {
        //Your first account 
        var testAccount = new Account(100.00);
        System.out.println("Initial balance: " + testAccount.getBalance().toString());
        testAccount.deposit(20.00);
        System.err.println("After deposit balance:" + testAccount.getBalance().toString());
        // Your first bank transfer
        var MatthewsAccount = new Account(1000.000);
        var MyAccount = new Account(0.0);
        MatthewsAccount.withdraw(100.00);
        MyAccount.deposit(10.00);
        System.out.println("Matthew account funds: " + MatthewsAccount.getBalance().toString());
        System.out.println("My Account funds: " + MyAccount.getBalance().toString());
        // Whistle
        var whistle = new Whistle("Kvaak");
        whistle.sound();
        //PaymentCard
        var card = new PaymentCard(50.00);
        System.out.println(card);
         card.eatAffordably();
        System.out.println(card);

        card.eatHeartily();
        card.eatAffordably();
        System.out.println(card);
        PaymentCard card2 = new PaymentCard(10.00);
        System.out.println(card2);
        card2.addMoney(15.00);
        System.out.println(card2);

        card2.addMoney(10.00);
        System.out.println(card2);

        card2.addMoney(200.00);
        System.out.println(card2);
        var PaulsCard = new PaymentCard(20.00);
        var MattsCard = new PaymentCard(30.00);
        PaulsCard.eatHeartily();
        MattsCard.eatAffordably();
        System.out.println("Matt's Card: " + MattsCard);
        System.out.println("Paul's Card: " + PaulsCard);
        PaulsCard.addMoney(20.00);
        MattsCard.eatHeartily();
        System.out.println("Matt's Card: " + MattsCard);
        System.out.println("Paul's Card: " + PaulsCard);
        PaulsCard.eatAffordably();
        PaulsCard.eatAffordably();
        MattsCard.addMoney(50.00);
        System.out.println("Matt's Card: " + MattsCard);
        System.out.println("Paul's Card: " + PaulsCard);
        //Object List
        var itemsList = new Items();
        var inputReader = new Scanner(System.in);
        System.out.println("Type only enter to continue");
        while(inputReader.nextLine().trim().equals("")){
            itemsList.addItem();
            System.out.println("Type something else to stop and print the shopping list");
        }
        inputReader.close();
        itemsList.printItems();
    }
}
